import os
import pygame
import time

base_path = os.path.dirname(__file__)

def dogru_1():
   pygame.init()
   pygame.mixer.init()
   ses_dosyasi = os.path.join(base_path, "dogru_1.wav")
   pygame.mixer.music.load(ses_dosyasi)
   pygame.mixer.music.play()  
   time.sleep(1)
   
